import React, { useState, useEffect } from 'react';
import Form from './Form';
import 'tailwindcss/tailwind.css';
import firebase from 'firebase/compat/app'; // update import statement
import 'firebase/compat/database'; 
import firebaseConfig from './firebaseConfig';

const App = () => {

  const [formData, setFormData] = useState([]);

  useEffect(() => {
    firebase.initializeApp(firebaseConfig);
    const databaseRef = firebase.database().ref('formSchema');
    databaseRef.on('value', (snapshot) => {
      const data = snapshot.val();
      const formFields = Object.keys(data).map((key) => {
        return { id: key, ...data[key] };
      });
      setFormData(formFields);

    });
  }, []);

  return (
    <div className="bg-[url('https://tailwindcss.com/_next/static/media/docs-dark@tinypng.1bbe175e.png')] bg-cover w-screen h-screen bg-[#0f172a] fixed">
      <div className="min-h-screen flex justify-center items-center">
        <div className="max-w-md w-full mx-auto container backdrop-opacity-9 backdrop-blur-lg bg-black/30 p-8 first-letter rounded-xl">
          <h1 className="text-2xl font-bold text-[#879cf1] mb-4">Register</h1>
          <Form formData={formData} />
        </div>
      </div>
    </div>
  );
};

export default App;
