import React, { useState} from 'react';

const Form = ({ formData }) => {

  const handleSubmit = (data) => {
    data.preventDefault();
    console.log(data);
  };

  const Tickbox = ({ label, name }) => {
    const [ticked, setTicked] = useState(false);

    const handleChange = (event) => {
      setTicked(!ticked);
    };

    return (
      <div className="form-group">
        <label htmlFor={name}>
          <input
            type="checkbox"
            id={name}
            name={name}
            checked={ticked}
            onChange={handleChange}
          />
          {label}
        </label>
      </div>
    );
  };

  const Checkbox = ({ label, name }) => {
    const [checked, setChecked] = useState(false);

    const handleChange = (event) => {
      setChecked(event.target.checked);
    };

    return (
      <div className="form-group content-center justify-center">
        <label htmlFor={name} className=' hover:text-cyan-600  text-slate-300 font-semibold'>
          <input
            type="checkbox"
            id={name}
            name={name}
            checked={checked}
            onChange={handleChange}
            className='m-3'
          />
          {label}
        </label>
      </div>
    );
  };

const renderInput = (fieldName, fieldData) => {
    switch (fieldData.type) {
      case "text":
      case "email":
      case "password":
      case "tel":
      case "number":
            return (
                  <div key={fieldName} className="mb-4">
                    <label className=" text-gray-400 text-sm font-bold mb-2" htmlFor={fieldName}>
                      {fieldData.label}
                    </label>
                    <input
                      className="shadow appearance-none rounded w-full py-2 px-3 text-white leading-tight focus:outline-1 caret-[#879cf1] bg-slate-600"
                      type={fieldData.type}
                      name={fieldName}
                      required={fieldData.required}
                    />
                  </div>
        );
      case "checkbox":
          return <Checkbox label={fieldData.label} name={fieldData.name} />;
      case "tickbox":
          return <Tickbox label={fieldData.label} name={fieldData.name} />;
      default:
        return null;

      }
  };



  const renderFormFields = () => {
    return Object.entries(formData).map(([fieldName, fieldData]) => {
      return renderInput(fieldName, fieldData);
    });
  };

  return (
    <form onSubmit={handleSubmit}>
      {renderFormFields()}
      <button type="submit" className=' container w-20 h-8 rounded-2xl content-center justify-center font-bold bg-[#879cf1] text-slate-900 bold'>Submit</button>
    </form>
  );
};

export default Form;
